package ink.ckx.leetcode.medium;

import java.util.HashMap;
import java.util.HashSet;
import java.util.Stack;

/**
 * @author chenkaixin
 * @description
 * @since 2021/12/15
 */
public class Solution_316 {

    /**
     * 给你一个字符串 s ，请你去除字符串中重复的字母，使得每个字母只出现一次。需保证 返回结果的字典序最小（要求不能打乱其他字符的相对位置）。
     * 注意：该题与 1081 https://leetcode-cn.com/problems/smallest-subsequence-of-distinct-characters 相同。
     */
    public static void main(String[] args) {
        String s = "";
        System.out.println(new Solution_316().removeDuplicateLetters(s));
    }

    public String removeDuplicateLetters(String s) {
        Stack<Character> stack = new Stack<>();
        HashSet<Character> seen = new HashSet<>();
        HashMap<Character, Integer> last_occur = new HashMap<>();
        for (int i = 0; i < s.length(); i++) {
            last_occur.put(s.charAt(i), i);
        }
        // 遍历字符串，判断是否入栈
        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if (!seen.contains(c)) {
                while (!stack.isEmpty() &&
                        c < stack.peek() &&
                        last_occur.get(stack.peek()) > i) {
                    seen.remove(stack.pop());
                }
                seen.add(c);
                stack.push(c);
            }
        }
        // 将栈中元素保存成字符串输出
        StringBuilder sb = new StringBuilder(stack.size());
        for (Character c : stack) {
            sb.append(c.charValue());
        }
        return sb.toString();
    }
}